	[2499] = {
		Id = "FourUniqueBodyDex15",
		Text = "Hyrri loosed a barrage of arrows,\ntipped with a poisoned hatred\nonly oppression can ferment.",
		Name = "Hyrri's Ire",
	},
	[2500] = {
		Id = "FourUniqueBodyInt1",
		Text = "Faith springs abundant at the edge of death.",
