	},
	[2503] = {
		Id = "FourUniqueBodyInt4",
		Text = "Fueled by the blackness in the hearts of men,\nthe armies of Saresh were just as relentless.",
		Name = "Necromantle",
	},
	[2504] = {
		Id = "FourUniqueBodyInt5",
