	},
	[2568] = {
		Id = "FourUniqueHelmetDex1",
		Text = "Beyond madness lies inspiration.",
		Name = "Innsmouth",
	},
	[2569] = {
		Id = "FourUniqueHelmetDex2",
		Text = "No metal slips as easily through the fingers as gold.",
		Name = "Goldrim",
	},
	[2570] = {
		Id = "FourUniqueHelmetDex3",
		Text = "No man burns alone.",
		Name = "Radiant Grief",
